mapper.update:
update act_proc_re_identity
act_id = #{actId,jdbcType=VARCHAR}, act_name = #{actName,jdbcType=VARCHAR}, proc_def_id = #{procDefId,jdbcType=VARCHAR}, proc_def_key = #{procDefKey,jdbcType=VARCHAR}, identity_type = #{identityType,jdbcType=VARCHAR}, identity_id = #{identityId,jdbcType=VARCHAR},
where id = #{id, jdbcType=INTEGER}

mapper.select:
select * from act_proc_re_identity
where
  proc_def_id = #{procDefId,jdbcType=VARCHAR}
and
  act_id = #{actId,jdbcType=VARCHAR}

select * from act_proc_re_identity
where
  proc_def_key = #{procDefKey,jdbcType=VARCHAR}
and
  act_id = #{actId,jdbcType=VARCHAR}
