mapper.update:
update act_proc_ru_identity
task_id = #{taskId,jdbcType=VARCHAR}, proc_def_id = #{procDefId,jdbcType=VARCHAR}, proc_def_key = #{procDefKey,jdbcType=VARCHAR}, business_key = #{businessKey,jdbcType=VARCHAR}, act_id = #{actId,jdbcType=VARCHAR}, identity_type = #{identityType,jdbcType=VARCHAR}, identity_id = #{identityId,jdbcType=VARCHAR}, user_id = #{userId,jdbcType=VARCHAR}
where id = #{id, jdbcType=INTEGER}

mapper.select:
select * from act_proc_ru_identity
where
  proc_def_id = #{procDefId,jdbcType=VARCHAR}
and
  act_id = #{actId,jdbcType=VARCHAR}

select * from act_proc_ru_identity
where
  proc_def_key = #{procDefKey,jdbcType=VARCHAR}
and
  act_id = #{actId,jdbcType=VARCHAR}
